mapper.update:
update rice_order
order_code = #{orderCode}, canteen_id = #{canteenId}, order_price = #{orderPrice}, order_paid = #{orderPaid}, status = #{status}, error_reason = #{errorReason}, create_time = #{createTime},
where order_id = #{orderId}

mapper.select:
select ro.order_id, ro.user_id, ro.order_code, ro.meal_number, ro.canteen_id, rc.canteen_name, rc.canteen_url, ro.order_price, ro.status, ro.error_reason, ro.create_time
from rice_order ro
left join rice_canteen rc on rc.canteen_id=ro.canteen_id

     and ro.user_id = #{userId}
     and order_code like concat('%', #{orderCode}, '%')
     and meal_number = #{mealNumber}
     and canteen_id = #{canteenId}
     and status = #{status}

order by ro.status asc, ro.create_time desc
